    public int sentiment = 0;

    void UpdateScore(ChangeEventArgs evt)
    {
        var text = (string)evt.Value;
        sentiment = SentimentAnalysis.Services.Sentiment.EvaluatePrediction(text);
    }

    string ResultText()
    {
        switch (sentiment)
        {
            case 1:
                return "Positive";
            case -1:
                return "Negative";
            case 0:
                return "Neutral";
            default:
                return "Neutral";
        }
    }

    int MarkerPosition()
    {
        switch (sentiment)
        {
            case 1:
                return 100;
            case -1:
                return 0;
            case 0:
                return 45;
            default:
                return 45;
        }
    }


